$_MODULE['<{ps_facebook}prestashop>psfacebooktranslations_a931bf01ccd127d4e49ae9989039c9cb'] = 'Das Facebook-Werbekonto ermöglicht es Händlern, auf ihr Werbungsverwaltungstool zuzugreifen und bezahlte Facebook-Werbekampagnen zu erstellen, zu bearbeiten und zu analysieren';
