$_MODULE['<{ps_facebook}prestashop>psfacebooktranslations_af8699a7a1f8ed9ab5cd942ca5a419a4'] = 'Eine kurze, aussagekräftige Beschreibung des Artikels. Nenne spezifische und einzigartige Features deiner Artikel, zum Beispiel Farbe oder Material des Produkts. Verwende regulären Text (kein HTML), verwende keine Schreibweise nur in Großbuchstaben und füge keine Links ein. Mehr dazu: Spezifikationen zu Produktbeschreibungen. Max. 5.000 Zeichen.';
$_MODULE['<{ps_facebook}prestashop>psfacebooktranslations_b5703b845e5aa6ab6940d1186da4929b'] = 'Gescheitert! Erneut versuchen';
